INSERT INTO `programs` (`id`, `title`, `slug`, `category_id`, `short_description`, `description`, `thumbnail_image`, `large_image`, `status`, `order`, `meta_title`, `meta_description`, `program_url`, `additional_details`, `created_at`, `updated_at`) VALUES
(1, 'BLS Certification', 'harum-modi-veniam-ea-sunt', 1, 'The BLS training for Healthcare Providers Course teaches the skills of CPR for victims of all ages (including ventilation with a barrier device, a bag-mask device, and oxygen), use of an AED on adults and children, and relief of choking in a safe, timely and effective manner.', 'The BLS training for Healthcare Providers Course teaches the skills of CPR for victims of all ages (including ventilation with a barrier device, a bag-mask device, and oxygen), use of an AED on adults and children, and relief of choking in a safe, timely and effective manner. The course is designed for healthcare providers who care for patients both in and out of the hospital. Taught by a certified AHA Instructor and Paramedic, this course is a great addition and compliment to the IV Contrast Injection Certification Training. Upon completion of our Basic Life Support Certification Course and passing the exam you\'ll receive your certificate and wallet card within 7-10 business days! This course will be scheduled to follow the IV Contrast Injection Certification Training creating a one stop shop for your certification needs.\n\nCourse Content:\n\nKey changes in basic life support, reflecting the new science from the updated American Heart Association Guidelines for Cardiopulmonary Resuscitation and Emergency Cardiovascular Care\nCritical concepts of high-quality CPR\n1-Rescuer CPR and AED for adult, child and infant\n2-Rescuer CPR and AED for adult, child and infant\nDifferences between adult, child and infant rescue techniques\nBag-mask techniques for adult, child and infant\nRescue breathing for adult, child and infant\nRelief of choking for adult, child and infant\nCPR with an advanced airway', 'programs/thumbnails/01KD88AFKDE3P3SKD3WG3HTBJ1.jpg', 'programs/large/01KD88AFKF9FS71EHR14YAG8VE.jpg', 'active', 0, 'Sed recusandae Aut fugiat quasi qui assumenda ullam vel voluptate sit consequat Pariatur Aut', 'Veniam tempore ea ', 'https://advancedimagingreview.com/course-details/bls-certification', '\"Id praesentium maxim\"', '2025-12-23 17:34:12', '2025-12-24 13:38:11'),
(2, 'Clinical Rotations Only', 'clinical-rotations-only', 1, 'Have you already completed a registry review course but lack clinical competencies? We\'ve got you covered. The Advanced Imaging Review clinical rotations only option allows students who have already completed an ASRT approved registry review to rotate through anyone of our facilities to obtain their competencies and become registry eligible. Below is a list of pre-requisites that are required to be considered for the program.', '<p><strong>H</strong>ave you already completed a registry review course but lack clinical competencies? We&#039;ve got you covered. The Advanced Imaging Review clinical rotations only option allows students who have already completed an ASRT approved registry review to rotate through anyone of our facilities to obtain their competencies and become registry eligible. Below is a list of pre-requisites that are required to be considered for the program.REQUIREMENTSCurrent state radiologic technologist licensure ( trainee must be licensed in the state of the clinical site)<br>Current ARRT registration<br>Completion of an ASRT approved registry review course within the last 12 months.<br>Candidate MUST be able to provide a copy of the course curriculum for reference.<br>Candidate MUST be IV contrast injection certified or enrolled in our IV Certification program (NYS Mandated Guidelines)Clinical placement timelines vary based on site approval requirements. Placement typically takes 3–4 weeks or longer to complete clearance and secure all necessary approvals.</p>', 'programs/thumbnails/01KD8A7R8MF165J6BF4E9RVTEG.jpg', 'programs/large/01KD8A7R8N3861KWY4J6P0EG1X.jpg', 'active', 0, NULL, NULL, 'https://advancedimagingreview.com/course-details/clinical-rotations-only', NULL, '2025-12-24 13:54:35', '2025-12-27 08:44:09'),
(3, 'Clinical Rotations Only', 'clinical-rotations-only1', 1, 'Have you already completed a registry review course but lack clinical competencies? We\'ve got you covered. The Advanced Imaging Review clinical rotations only option allows students who have already completed an ASRT approved registry review to rotate through anyone of our facilities to obtain their competencies and become registry eligible. Below is a list of pre-requisites that are required to be considered for the program.', 'Have you already completed a registry review course but lack clinical competencies? We\'ve got you covered. The Advanced Imaging Review clinical rotations only option allows students who have already completed an ASRT approved registry review to rotate through anyone of our facilities to obtain their competencies and become registry eligible. Below is a list of pre-requisites that are required to be considered for the program. \n\n \n\nREQUIREMENTS\n\nCurrent state radiologic technologist licensure ( trainee must be licensed in the state of the clinical site)\nCurrent ARRT registration\nCompletion of an ASRT approved registry review course within the last 12 months.\nCandidate MUST be able to provide a copy of the course curriculum for reference. \nCandidate MUST be IV contrast injection certified or enrolled in our IV Certification program (NYS Mandated Guidelines)\n \n\nClinical placement timelines vary based on site approval requirements. Placement typically takes 3–4 weeks or longer to complete clearance and secure all necessary approvals.', 'programs/thumbnails/01KD8A7R8MF165J6BF4E9RVTEG.jpg', 'programs/large/01KD8A7R8N3861KWY4J6P0EG1X.jpg', 'active', 0, NULL, NULL, 'https://advancedimagingreview.com/course-details/clinical-rotations-only', NULL, '2025-12-24 13:54:35', '2025-12-24 13:54:35'),
